(function(d3, fc, sc) {
    'use strict';

    // Generates a menu option similar to those generated by sc.model.menu.option from a sc.model.data.period object
    sc.model.menu.periodAdaptor = function(period) {
        return {
            displayString: period.display,
            option: period
        };
    };

}(d3, fc, sc));
